import React, { useState, useEffect } from 'react';
import "./Carousel.css";
import { useSwipeable } from "react-swipeable";




const carousel = {
    marginTop: "2rem",
    paddingTop: "2rem",
    paddingBottom: "2rem",
    overflow: "hidden",
    backgroundColor: "yellow",
    width: "100%"
}





export const CarouselItem = ({ children, width }) => {
    console.log("CarouselItem: ", children)
    return (<div style={{
        width: width,
        display: "inline-flex",
        alignItems: "center",
        justifyContent: "center",
        height: "500px",
        border: "2px solid red",
        margin: "0",
        padding: "0",
        alignSelf: "center"

    }}>
        {children}
    </div>
    );
}


const Carousel = ({ children }) => {


    let dumArr = new Array(Math.ceil(children.length / 2))
    const [paused, setPaused] = useState(false);
    const [activeIndex, setActiveIndex] = useState(0);

    const updateIndex = (newIndex) => {
        if (newIndex < 0) {
            newIndex = React.Children.count(children) - 2;

        } else if (newIndex >= React.Children.count(children) / 2) {
            newIndex = 0;
        }

        setActiveIndex(newIndex);
    }

    useEffect(() => {
        const interval = setInterval(() => {
            if (!paused) {
                updateIndex(activeIndex + 1);
            }
        }, 3000);

        return () => {
            if (interval) {
                clearInterval(interval);
            }
        };
    })

    const handlers = useSwipeable({
        onSwipedLeft: () => updateIndex(activeIndex + 1),
        onSwipedRight: () => updateIndex(activeIndex - 1)
    })

    return (
        <div {...handlers}
            style={carousel} onMouseEnter={() => setPaused(true)} onMouseLeave={() => setPaused(false)}>
            <div style={{
                transform: `translateX(-${activeIndex * 100}%)`,
                whiteSpace: "nowrap",
                transition: "transform 0.3s",

            }}>
                {
                    React.Children.map(children, (child, index) => {
                        return React.cloneElement(child, { width: "50%" });
                    })}
            </div>
            <div className="indicators">
                <button className="arrorButtonLink" onClick={() => {
                    console.log(activeIndex)
                    return updateIndex(activeIndex - 1)
                }}>
                    Prev
            </button>
                {
                    React.Children.map(dumArr, (child, index) => {
                        return (
                            <button
                                className={`${index === activeIndex ? "active" : ""}`}
                                onClick={() => {
                                    console.log("index: ", index)
                                    updateIndex(index)
                                }}>
                                {index + 1}
                            </button>
                        )
                    })
                }
                <button className="arrorButtonLink" onClick={() => updateIndex(activeIndex + 1)}>
                    Next
            </button>

            </div>
        </div>
    );
}


// #endregion

export default Carousel;
