import React, { useState } from 'react';
import "./Carousel.css";


const carouselItem = {
    width: "500px",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    height: "500px",
    backgroundColor: "green",
    color: "#fff",
    margin: "1em"
}

const carousel = {
    overflow: "hidden",
    backgroundColor: "yellow"
}

const innerCarousel = {
    transform: "translateX(-0%)",
    whiteSpace: "nowrap",
    transition: "transform 0.3s",
    display: "flex",
    justifyContent: "center"
}

export const CarouselItem = ({ children, width }) => {
    return (<div style={{
        width: width,
        display: "inline-flex",
        alignItems: "center",
        justifyContent: "center",
        height: "500px",
        backgroundColor: "green",
        color: "#fff",
        margin: "1em"
    }}>
        {children}
    </div>
    );
}


const Carousel = ({ children }) => {
    const [activeIndex, setActiveIndex] = useState(0);

    return (
        <div style={carousel}>
            <div style={{
                transform: `translateX(-${activeIndex * 100}%)`,
                whiteSpace: "nowrap",
                transition: "transform 0.3s",
                display: "flex",
                justifyContent: "center"
            }}>
                {
                    React.Children.map(children, (child, index) => {
                        return React.cloneElement(child, { width: "500px" });
                    })}
            </div>
        </div>
    );
}


// #endregion

export default Carousel;
